import sys, os
sys.path.append(os.path.dirname(os.path.dirname(os.path.abspath(__file__))))# scripts/make_image.py import argparse from pathlib import Path from src.utils import parse_complex from src.render import render_image  def main():     ap = argparse.ArgumentParser()     ap.add_argument("--map", type=str, default="quadratic", help="quadratic or exponential")     ap.add_argument("--c", type=str, default="0+0j", help="complex constant, e.g. -0.4+0.6j")     ap.add_argument("--xmin", type=float, required=True)     ap.add_argument("--xmax", type=float, required=True)     ap.add_argument("--ymin", type=float, required=True)     ap.add_argument("--ymax", type=float, required=True)     ap.add_argument("--width", type=int, default=900)     ap.add_argument("--height", type=int, default=900)     ap.add_argument("--max_iter", type=int, default=200)     ap.add_argument("--escape", type=float, default=1e6)     ap.add_argument("--color", type=str, default="iters", help="iters, angle, smooth")     ap.add_argument("--power_hint", type=float, default=2.0, help="use 2.0 for quadratic, try 3â€“6 for exponential")     ap.add_argument("--outfile", type=str, required=True)     args = ap.parse_args()      img = render_image(         map_name=args.map,         c=parse_complex(args.c),         xmin=args.xmin, xmax=args.xmax, ymin=args.ymin, ymax=args.ymax,         width=args.width, height=args.height,         max_iter=args.max_iter,         escape_radius=args.escape,         color_mode=args.color,         power_hint=args.power_hint     )     outpath = Path(args.outfile)     outpath.parent.mkdir(parents=True, exist_ok=True)     img.save(outpath)     print(f"Saved {outpath}")  if __name__ == "__main__":     main()
